script.source:
ICoreVisitor v = coreAccess.createVisitor()

def metricId = coreAccess.getOrCreateMetricId("PercentageCodeLargeClasses", "Percentage of Statements in Large Classes", "Percentage of statements in classes with more than ${parameterTopNumStatements} statements", true)
def largeClassesNos = 0
def moduleNos = 0

def systemLargeClassesNos = 0
def systemNos = 0


v.onType
{
    TypeAccess type ->

    Number nos = type.getNumberOfStatementsMetric()

    if(nos != null)
    {
        if (nos > parameterTopNumStatements)
        {
            largeClassesNos += nos
            systemLargeClassesNos += nos
        }

        systemNos += nos
        moduleNos += nos
    }
}

v.onModule
{
    ModuleAccess module ->

    v.visitChildren(module)
    if (moduleNos == 0)
    {
        return
    }

    def value = 100.0 * ((double) largeClassesNos / moduleNos)
    result.addMetricValue(metricId, module, value)
    largeClassesNos = 0
    moduleNos = 0
}

coreAccess.visitParserModel(v)

if(systemNos > 0)
{
    def value = 100.0 * ((double) systemLargeClassesNos / systemNos)
    result.addMetricValue(metricId, coreAccess, value)
}
